sp_widget.client_script:
function($scope) {
  /* widget controller */
  var c = this;

	//console.log("$scope.data.employeeCount: "+ $scope.data.employeeCount);
	console.log("$scope.data.reportCount: "+ $scope.data.reportCount);
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var gr = new GlideAggregate('x_entg_hhs_poc_employees');
	gr.addAggregate('COUNT');
	gr.query();
	data.employeeCount = 0;
	if(gr.next())
		data.employeeCount = gr.getAggregate('COUNT');

	gr = new GlideAggregate('x_entg_hhs_poc_outside_activity_annual_report');
	gr.addAggregate('COUNT');
	gr.query();
	data.reportCount = 0;
	if(gr.next())
		data.reportCount = gr.getAggregate('COUNT');



})();
