(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
data.sys_id = $sp.getParameter('sys_id');

	var grForm = new GlideRecord('x_entg_hhs_poc_outside_activity_request_520');
	grForm.get(data.sys_id);
	data.form = {};


	var arrayFields = 'activity_type,activity_type_desc,actvity_duty_relation,advance,annual_report,approved,approved_w_comments,compensated,comp_amount,consultaiton_desc,consultation,date_filed,days_per_week,denied,disclaimer_text,disposition,employee,employer_duty_effect,end_date,entity_cell,entity_city,entity_email,entity_fax,entity_state,entity_street,entity_telephone,entity_zip,estimated_amount,ethics_official_cell,ethics_official_email,ethics_official_fax,ethics_official_name,ethics_official_tele,ethics_official_title,ethics_organization,explanation,fee,filing_status,final_sig,first_approval,hhs_funded,hhs_funded_desc,honorarium,hours_per_day,initial_sig,initial_submission,non_travel,non_travel_desc,official_duty_desc,official_duty_desc_attached,other_comp,other_comp_desc,outside_contact,outside_employer_assignments,outside_entity_name,outside_title,payor,recommend_approval,recommend_disapproval,rec_disapproval,retainer,royalty,salary,second_approval,self_employed_desc,self_employed_ind,start_date,status,stock,stock_options,subject_matter,supervisor_statement,sys_id,travel,travel_desc,travel_detail,usual_working_hours,usual_working_hours_desc,weeks_per_year';

	var referenceFields = ',employee.agency,employee.appointment_type,employee.appointment_type,employee.street,employee.city,employee.zip,employee.state,employee.position_title';
	$sp.getRecordDisplayValues(data.form, grForm, arrayFields + referenceFields)

	data.test = grForm.employee.getDisplayValue();
})();
